pragma solidity ^0.8.12;

import {OwnableUpgradeable} from "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";
import {Initializable} from "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import {UUPSUpgradeable} from "@openzeppelin/contracts-upgradeable/proxy/utils/UUPSUpgradeable.sol";
import {ERC1967Proxy} from "@openzeppelin/contracts/proxy/ERC1967/ERC1967Proxy.sol";

import {DeVoxShamanV1} from "./DeVoxShamanV1.sol";
import {IShaman} from "./IShaman.sol";
import {IShamanSummoner} from "./IShamanSummoner.sol";

contract DeVoxShamanSummonerV1 is
    Initializable,
    OwnableUpgradeable,
    UUPSUpgradeable,
    IShamanSummoner
{
    /// @notice Current version of the contract
    uint16 internal _version;

    /// @notice Current version of the contract
    uint256 internal _id;

    address payable public template;

    /*******************
     * EVENTS
     ******************/

    /// @notice emitted when a new shaman is summoned
    /// @param baal Baal contract address
    /// @param shaman Shaman contract address
    /// @param token ERC20 token address
    /// @param userRegistry UserRegistry contract address
    /// @param id Id of the campaign
    /// @param pricePerUnit Raw amount of ERC20 required per accounting unit (USD)
    /// @param tokensPerUnit Amount of tokens issued per accounting unit (USD)
    /// @param target Target amount of accounting unit to be raised
    /// @param name Name of the campaign
    event SummonComplete(
        address indexed baal,
        address indexed shaman,
        address token,
        address userRegistry,
        uint256 id,
        uint256 pricePerUnit,
        uint256 tokensPerUnit,
        uint256 target,
        string name
    );

    /// @notice Contract constructor logic
    /// @custom:oz-upgrades-unsafe-allow constructor
    constructor() {
        _disableInitializers();
    }

    /// @notice Contract initialization logic
    function initialize(address payable _template) public initializer {
        __Ownable_init();
        __UUPSUpgradeable_init();

        template = _template;
    }

    function summonDeVoxShaman(
        address _baal,
        bytes calldata _initializationParams
    ) external override returns (address) {
        _id = _id + 1;

        (
            address _token,
            address _userRegistry,
            uint256 _pricePerUnit,
            uint256 _tokensPerUnit,
            uint256 _target,
            string memory _name,
            address[] memory _admins
        ) = abi.decode(
                _initializationParams,
                (address, address, uint256, uint256, uint256, string, address[])
            );

        require(
            _baal != address(0),
            "DeVoxShamanSummonerV1: _baal cannot be 0x0"
        );
        require(
            _token != address(0),
            "DeVoxShamanSummonerV1: _token cannot be 0x0"
        );

        address shaman = address(
            new ERC1967Proxy(
                template,
                abi.encodeWithSelector(
                    IShaman(template).initialize.selector,
                    _baal,
                    _token,
                    _userRegistry,
                    _id,
                    _pricePerUnit,
                    _tokensPerUnit,
                    _target,
                    _admins
                )
            )
        );

        require(
            address(DeVoxShamanV1(shaman).token()) == address(_token),
            "DeVoxShamanSummonerV1: token mismatch"
        );

        emit SummonComplete(
            _baal,
            shaman,
            _token,
            _userRegistry,
            _id,
            _pricePerUnit,
            _tokensPerUnit,
            _target,
            _name
        );

        return shaman;
    }

    /// @notice gets the current version of the contract
    function version() public view virtual returns (uint256) {
        return _version;
    }

    /// @notice Update the contract version number
    /// @dev onlyOwner
    function updateVersion() external onlyOwner {
        _version += 1;
    }

    /*******************
     * INTERNAL
     ******************/

    /// @notice upgrade authorization logic
    /// @dev adds onlyOwner requirement
    function _authorizeUpgrade(
        address /*newImplementation*/
    )
        internal
        view
        override
        onlyOwner // solhint-disable-next-line no-empty-blocks
    {
        //empty block
    }
}
","keccak256":"0xb32aad0908182a0a3c8c944b3a0f8310c524dd7e7ccf4476f7e999a1c148bb50","license":"GPL-3.0-or-later"},"contracts/shaman/DeVoxShamanV1.sol":{"content":"// SPDX-License-Identifier: GPL-3.0-or-later
pragma solidity ^0.8.12;

import {IERC20} from "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import {AccessControlUpgradeable} from "@openzeppelin/contracts-upgradeable/access/AccessControlUpgradeable.sol";
import {OwnableUpgradeable} from "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";
import {Initializable} from "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import {UUPSUpgradeable} from "@openzeppelin/contracts-upgradeable/proxy/utils/UUPSUpgradeable.sol";
import {ReentrancyGuardUpgradeable} from "@openzeppelin/contracts-upgradeable/security/ReentrancyGuardUpgradeable.sol";
import {Clones} from "@openzeppelin/contracts/proxy/Clones.sol";

import {IBaal} from "../baal/interfaces/IBaal.sol";
import {FixedPointMathLib} from "../lib/FixedPointMathLib.sol";
import {IUserRegistry} from "./IUserRegistry.sol";
import {IShaman} from "./IShaman.sol";

// import "hardhat/console.sol";

/// @notice Shaman contract for Baal v3 DAOhaus
/// @dev This contract is used to issue voting shares (quadratic) and loot (1:1) for donations
contract DeVoxShamanV1 is
    Initializable,
    AccessControlUpgradeable,
    OwnableUpgradeable,
    ReentrancyGuardUpgradeable,
    UUPSUpgradeable,
    IShaman
{
    enum TokenType {
        LOOT,
        SHARES
    }

    /// @notice Current version of the contract
    uint16 internal _version;

    IBaal public baal;
    IERC20 public token;
    IUserRegistry public userRegistry;
    uint256 public id;
    uint256 public pricePerUnit;
    uint256 public tokensPerUnit;
    uint256 public target;

    /// @notice Donations made by address
    mapping(address => uint256) public donations;

    /// @notice Signatures made by address
    mapping(address => bool) public signatures;

    /*******************
     * EVENTS
     ******************/

    /// @notice emitted when a donation is received
    /// @param contributorAddress wallet sending the donation
    /// @param baal DAO contract address
    /// @param id campaign id
    /// @param amount amount donated
    /// @param total total donated from this wallet
    /// @param target campaign target amount
    /// @param balance current campaign balance
    /// @param lootIssued loot issued for this donation
    /// @param sharesIssued shares issued for this donation
    /// @param message message accompanying the donation
    event DonationReceived(
        address indexed contributorAddress,
        address indexed baal,
        uint256 indexed id,
        uint256 amount,
        uint256 total,
        uint256 target,
        uint256 balance,
        uint256 lootIssued,
        uint256 sharesIssued,
        string message
    );

    /// @notice emitted when campaign target is updated
    /// @param baal baal contract address
    /// @param id campaign id
    /// @param target campaign target amount
    /// @param balance current campaign balance
    event TargetUpdated(
        address indexed baal,
        uint indexed id,
        uint256 target,
        uint256 balance
    );

    /// @notice emitted when a user signs the campaign
    /// @param user user address
    /// @param baal baal contract address
    /// @param id campaign id
    event UserSigned(
        address indexed user,
        address indexed baal,
        uint indexed id
    );

    /// @notice emitted when a user is whitelisted
    /// @param user user address
    /// @param baal baal contract address
    /// @param id campaign id
    /// @param status whitelist status
    /// @param metadata user metadata
    event UserWhitelisted(
        address indexed user,
        address indexed baal,
        uint indexed id,
        bool status,
        bytes metadata
    );

    /*******************
     * DEPLOY
     ******************/

    /// @notice Contract constructor logic
    /// @custom:oz-upgrades-unsafe-allow constructor
    constructor() {
        _disableInitializers();
    }

    /// @notice Contract initialization logic
    function initialize(
        address _moloch,
        address _token,
        address _userRegistry,
        uint256 _id,
        uint256 _pricePerUnit,
        uint256 _tokensPerUnit,
        uint256 _target,
        address[] calldata _admins
    ) external initializer {
        __AccessControl_init();
        __Ownable_init();
        __UUPSUpgradeable_init();

        baal = IBaal(_moloch);
        token = IERC20(_token);
        userRegistry = IUserRegistry(_userRegistry);
        id = _id;
        pricePerUnit = _pricePerUnit;
        tokensPerUnit = _tokensPerUnit;
        target = _target;

        // set admins
        for (uint256 i = 0; i < _admins.length; i++) {
            _grantRole(DEFAULT_ADMIN_ROLE, _admins[i]);
        }
        _grantRole(DEFAULT_ADMIN_ROLE, msg.sender);

        // console.log("DeVoxShamanV1 deployed by %s", msg.sender);
    }

    /// @notice Make a donation, join the DAO and receive voting shares
    /// @param _value amount donated
    /// @param _message message accompanying donation
    /// @dev message sender must be whitelisted
    function donate(
        uint256 _value,
        string calldata _message
    ) external override nonReentrant {
        require(address(baal) != address(0), "donate: !baal");
        require(address(token) != address(0), "donate: !token");
        require(baal.isManager(address(this)), "donate: shaman not manager");
        require(userRegistry.getUser(msg.sender), "donate: sender not registered");
        require(_value % pricePerUnit == 0, "donate: invalid amount"); // require value as multiple of units

        // send to DAO
        require(
            token.transferFrom(msg.sender, baal.target(), _value),
            "donate: transfer failed"
        );

        uint256 lootIssued = _lootToIssue(_value);
        _mintTokens(msg.sender, lootIssued, TokenType.LOOT);

        uint256 sharesIssued = _sharesToIssue(_value);
        _mintTokens(msg.sender, sharesIssued, TokenType.SHARES);

        uint256 total = donations[msg.sender];
        total = total + _value;
        donations[msg.sender] = total;

        emit DonationReceived(
            msg.sender,
            address(baal),
            id,
            _value,
            total,
            target,
            getTokenBalance(),
            lootIssued,
            sharesIssued,
            _message
        );
    }

    /// @notice Gets the total token balance of the contract
    function getTokenBalance() public returns (uint256) {
        return token.balanceOf(baal.target());
    }

    /// @notice gets the current version of the contract
    function version() public view virtual returns (uint256) {
        return _version;
    }

    /*******************
     * ADMIN
     ******************/

    /// @notice Cancel the specified proposal
    /// @param proposalId proposal id
    function cancelProposal(
        uint32 proposalId
    ) external override onlyRole(DEFAULT_ADMIN_ROLE) {
        require(baal.isManager(address(this)), "donate: shaman not manager");
        baal.cancelProposal(proposalId);
    }

    /// @notice Grant the specified user admin privileges
    /// @param user user address
    function setAdmin(
        address user
    ) external override onlyRole(DEFAULT_ADMIN_ROLE) {
        _grantRole(DEFAULT_ADMIN_ROLE, user);
    }

    function sign() external override {
        require(userRegistry.getUser(msg.sender), "sign: sender not registered");
        require(!signatures[msg.sender], "sign: already signed");

        signatures[msg.sender] = true;

        emit UserSigned(msg.sender, address(baal), id);
    }

    /// @notice Update campaign target
    /// @param _target campaign target amount
    function setTarget(uint256 _target) public onlyRole(DEFAULT_ADMIN_ROLE) {
        target = _target;

        emit TargetUpdated(address(baal), id, _target, getTokenBalance());
    }

    /*******************
     * OWNER
     ******************/

    /// @notice Update the contract version number
    /// @dev onlyOwner
    function updateVersion() external onlyOwner {
        _version += 1;
    }

    /*******************
     * INTERNAL
     ******************/

    /// @notice Calculate the amount of loot to issue for a given donation
    function _lootToIssue(
        uint256 //_value
    ) internal view virtual returns (uint256) {
        return 0;
    }

    /// @notice Calculate the amount of shares to issue for a given donation
    /// @dev Use a square root function to give a Quadratic Voting effect
    function _sharesToIssue(
        uint256 _value
    ) internal view virtual returns (uint256) {
        return FixedPointMathLib.sqrt(_value / pricePerUnit) * tokensPerUnit;
    }

    /// @notice Mint tokens for a given address
    /// @param to Recipient of tokens
    /// @param amount Amount of tokens to mint
    /// @param _tokenType Shares or Loot
    function _mintTokens(
        address to,
        uint256 amount,
        TokenType _tokenType
    ) private {
        address[] memory _receivers = new address[](1);
        _receivers[0] = to;

        uint256[] memory _amounts = new uint256[](1);
        _amounts[0] = amount;

        if (_tokenType == TokenType.SHARES) {
            baal.mintShares(_receivers, _amounts);
        } else {
            baal.mintLoot(_receivers, _amounts);
        }
    }

    /// @notice upgrade authorization logic
