pragma solidity ^0.8.12;

// based on https://github.com/Rari-Capital/solmate/blob/main/src/utils/FixedPointMathLib.sol
library FixedPointMathLib {
    function sqrt(uint256 x) internal pure returns (uint256 z) {
        assembly {
            // This segment is to get a reasonable initial estimate for the Babylonian method.
            // If the initial estimate is bad, the number of correct bits increases ~linearly
            // each iteration instead of ~quadratically.
            // The idea is to get z*z*y within a small factor of x.
            // More iterations here gets y in a tighter range. Currently, we will have
            // y in [256, 256*2^16). We ensure y>= 256 so that the relative difference
            // between y and y+1 is small. If x < 256 this is not possible, but those cases
            // are easy enough to verify exhaustively.
            z := 181 // The 'correct' value is 1, but this saves a multiply later
            let y := x
            // Note that we check y>= 2^(k + 8) but shift right by k bits each branch,
            // this is to ensure that if x >= 256, then y >= 256.
            if iszero(lt(y, 0x10000000000000000000000000000000000)) {
                y := shr(128, y)
                z := shl(64, z)
            }
            if iszero(lt(y, 0x1000000000000000000)) {
                y := shr(64, y)
                z := shl(32, z)
            }
            if iszero(lt(y, 0x10000000000)) {
                y := shr(32, y)
                z := shl(16, z)
            }
            if iszero(lt(y, 0x1000000)) {
                y := shr(16, y)
                z := shl(8, z)
            }
            // Now, z*z*y <= x < z*z*(y+1), and y <= 2^(16+8),
            // and either y >= 256, or x < 256.
            // Correctness can be checked exhaustively for x < 256, so we assume y >= 256.
            // Then z*sqrt(y) is within sqrt(257)/sqrt(256) of x, or about 20bps.

            // The estimate sqrt(x) = (181/1024) * (x+1) is off by a factor of ~2.83 both when x=1
            // and when x = 256 or 1/256. In the worst case, this needs seven Babylonian iterations.
            z := shr(18, mul(z, add(y, 65536))) // A multiply is saved from the initial z := 181

            // Run the Babylonian method seven times. This should be enough given initial estimate.
            // Possibly with a quadratic/cubic polynomial above we could get 4-6.
            z := shr(1, add(z, div(x, z)))
            z := shr(1, add(z, div(x, z)))
            z := shr(1, add(z, div(x, z)))
            z := shr(1, add(z, div(x, z)))
            z := shr(1, add(z, div(x, z)))
            z := shr(1, add(z, div(x, z)))
            z := shr(1, add(z, div(x, z)))

            // See https://en.wikipedia.org/wiki/Integer_square_root#Using_only_integer_division.
            // If x+1 is a perfect square, the Babylonian method cycles between
            // floor(sqrt(x)) and ceil(sqrt(x)). This check ensures we return floor.
            // The solmate implementation assigns zRoundDown := div(x, z) first, but
            // since this case is rare, we choose to save gas on the assignment and
            // repeat division in the rare case.
            // If you don't care whether floor or ceil is returned, you can skip this.
            if lt(div(x, z), z) {
                z := div(x, z)
            }
        }
    }
}","keccak256":"0xed477070cb858f9eb1aeb21fba45247e1d621c7ed2baad45e14069aed1242edb","license":"GPL-3.0-or-later"},"contracts/shaman/DeVoxShamanV1.sol":{"content":"// SPDX-License-Identifier: GPL-3.0-or-later
pragma solidity ^0.8.12;

import {IERC20} from "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import {OwnableUpgradeable} from "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";
import {Initializable} from "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import {UUPSUpgradeable} from "@openzeppelin/contracts-upgradeable/proxy/utils/UUPSUpgradeable.sol";
import {ReentrancyGuardUpgradeable} from "@openzeppelin/contracts-upgradeable/security/ReentrancyGuardUpgradeable.sol";
import {Clones} from "@openzeppelin/contracts/proxy/Clones.sol";

import {IBaal} from "../fixtures/Baal/interfaces/IBaal.sol";
import {FixedPointMathLib} from "../lib/FixedPointMathLib.sol";
import {IShaman} from "./IShaman.sol";

// import "hardhat/console.sol";

/// @notice Shaman contract for Baal v3 DAOhaus
/// @dev This contract is used to issue voting shares (quadratic) and loot (1:1) for donations
contract DeVoxShamanV1 is
    Initializable,
    OwnableUpgradeable,
    ReentrancyGuardUpgradeable,
    UUPSUpgradeable,
    IShaman
{
    enum TokenType {
        LOOT,
        SHARES
    }

    /// @notice Role required in order to access admin methods
    // bytes32 public constant DEFAULT_ADMIN_ROLE =
    //     keccak256('DEFAULT_ADMIN_ROLE');
    /// @notice Current version of the contract
    uint16 internal _version;

    IBaal public baal;
    IERC20 public token;
    uint256 public id;
    uint256 public pricePerUnit;
    uint256 public tokensPerUnit;
    uint256 public target;

    /// @notice Last cookie claims made by members
    /// @dev This is only a cache and claims older than period are deleted
    mapping(address => uint256) public donations;

    /// @notice Whitelist of addresses that can join the DAO
    mapping(address => bool) private _whitelist;

    /*******************
     * EVENTS
     ******************/

    /// @notice emitted when a donation is received
    /// @param contributorAddress wallet sending the donation
    /// @param baal DAO contract address
    /// @param id campaign id
    /// @param amount amount donated
    /// @param total total donated from this wallet
    /// @param target campaign target amount
    /// @param balance current campaign balance
    /// @param lootIssued loot issued for this donation
    /// @param sharesIssued shares issued for this donation
    /// @param message message accompanying the donation
    event DonationReceived(
        address indexed contributorAddress,
        address baal,
        uint256 id,
        uint256 amount,
        uint256 total,
        uint256 target,
        uint256 balance,
        uint256 lootIssued,
        uint256 sharesIssued,
        string message
    );

    /// @notice emitted when campaign target is updated
    /// @param id campaign id
    /// @param target campaign target amount
    /// @param balance current campaign balance
    event TargetUpdated(uint256 id, uint256 target, uint256 balance);

    /// @notice emitted when a user is whitelisted
    /// @param user user address
    /// @param status whitelist status
    /// @param metadata user metadata
    event UserWhitelisted(address indexed user, bool status, bytes metadata);

    /*******************
     * DEPLOY
     ******************/

    /// @notice Contract constructor logic
    /// @custom:oz-upgrades-unsafe-allow constructor
    constructor() {
        _disableInitializers();
    }

    /// @notice Contract initialization logic
    function initialize(
        address _moloch,
        address payable _token,
        uint256 _id,
        uint256 _pricePerUnit,
        uint256 _tokensPerUnit,
        uint256 _target
    ) external initializer {
        __Ownable_init();
        __UUPSUpgradeable_init();

        baal = IBaal(_moloch);
        token = IERC20(_token);
        id = _id;
        pricePerUnit = _pricePerUnit;
        tokensPerUnit = _tokensPerUnit;
        target = _target;
    }

    /// @notice Set BaaL contract address
    /// @param _baal BaaL contract address
    function setBaal(address _baal) external override /*onlyOwner*/ {
        baal = IBaal(_baal);
    }

    /// Whitelist a user, enabling them to join the DAO
    /// @param _status whitelist status
    /// @param _metadata user metadata
    function whitelist(
        bool _status,
        bytes calldata _metadata
    ) external override {
        require(
            _whitelist[msg.sender] != _status,
            "whitelist status unchanged"
        );

        _whitelist[msg.sender] = _status;

        emit UserWhitelisted(msg.sender, _status, _metadata);
    }

    /// @notice Make a donation, join the DAO and receive voting shares
    /// @param _value amount donated
    /// @param _message message accompanying donation
    /// @dev message sender must be whitelisted
    function donate(
        uint256 _value,
        string calldata _message
    ) external override nonReentrant {
        require(_whitelist[msg.sender], "user not whitelisted");
        require(address(baal) != address(0), "!init");
        require(baal.isManager(address(this)), "Shaman not manager");
        require(_value % pricePerUnit == 0, "invalid amount"); // require value as multiple of units

        // send to DAO
        require(
            token.transferFrom(msg.sender, baal.target(), _value),
            "Transfer failed"
        );

        uint256 lootIssued = _lootToIssue(_value);
        _mintTokens(msg.sender, lootIssued, TokenType.LOOT);

        uint256 sharesIssued = _sharesToIssue(_value);
        _mintTokens(msg.sender, sharesIssued, TokenType.SHARES);

        uint256 total = donations[msg.sender];
        total = total + _value;
        donations[msg.sender] = total;

        emit DonationReceived(
            msg.sender,
            address(baal),
            id,
            _value,
            total,
            target,
            getTokenBalance(),
            lootIssued,
            sharesIssued,
            _message
        );
    }

    /// @notice Calculate the amount of loot to issue for a given donation
    function _lootToIssue(
        uint256 //_value
    ) internal view virtual returns (uint256) {
        return 0;
    }

    /// @notice Calculate the amount of shares to issue for a given donation
    /// @dev Use a square root function to give a Quadratic Voting effect
    function _sharesToIssue(
        uint256 _value
    ) internal view virtual returns (uint256) {
        return FixedPointMathLib.sqrt(_value / pricePerUnit) * tokensPerUnit;
    }

    /// @notice Mint tokens for a given address
    /// @param to Recipient of tokens
    /// @param amount Amount of tokens to mint
    /// @param _tokenType Shares or Loot
    function _mintTokens(
        address to,
        uint256 amount,
        TokenType _tokenType
    ) private {
        address[] memory _receivers = new address[](1);
        _receivers[0] = to;

        uint256[] memory _amounts = new uint256[](1);
        _amounts[0] = amount;

        if (_tokenType == TokenType.SHARES) {
            baal.mintShares(_receivers, _amounts);
        } else {
            baal.mintLoot(_receivers, _amounts);
        }
    }

    /// @notice Gets the total token balance of the contract
    function getTokenBalance() public returns (uint256) {
        return token.balanceOf(baal.target());
    }

    function setTarget(uint256 _target) public {
        target = _target;
    }

    /// @notice gets the current version of the contract
    function version() public view virtual returns (uint256) {
        return _version;
    }

    /// @notice Update the contract version number
    /// @dev onlyOwner
    function updateVersion() external onlyOwner {
        _version += 1;
    }

    /*******************
     * INTERNAL
     ******************/

    /// @notice upgrade authorization logic
    /// @dev adds onlyOwner requirement
    function _authorizeUpgrade(
        address /*newImplementation*/
    )
        internal
        view
        override
        onlyOwner // solhint-disable-next-line no-empty-blocks
    {
        //empty block
    }
}
","keccak256":"0x9e7fb13919923ba4d08ac62b00cf2a8f7c29b4e1073ccf7df1fbdaa60e8eaf17","license":"GPL-3.0-or-later"},"contracts/shaman/IShaman.sol":{"content":"// SPDX-License-Identifier: GPL-3.0-or-later
pragma solidity ^0.8.12;

interface IShaman {
    /// @notice Contract initialization logic
    function initialize(
        address _moloch,
        address payable _token,
        uint256 _id,
        uint256 _pricePerUnit,
        uint256 _tokensPerUnit,
        uint256 _target
    ) external;

    /// @notice Set BaaL contract address
    /// @param _baal BaaL contract address
    function setBaal(address _baal) external;

    /// @notice Make a donation, join the DAO and receive voting shares
    /// @param _value amount donated
    /// @param _message message accompanying donation
    function donate(uint256 _value, string calldata _message) external;

    /// Whitelist a user, enabling them to join the DAO
    /// @param _status whitelist status
    /// @param _metadata user metadata
    function whitelist(bool _status, bytes calldata _metadata) external;
}
